from tkinter import *
from tkinter import ttk
import sqlite3
import pwd, os
import base64


root = Tk()

class Application():

        def __init__(self):
            self.root = root
            self.tela_login()
            self.widgets_login()
            root.mainloop() #Informação básica necessária para se abrir uma janela no Tkinter 

        def tela_login(self):
            self.root.title("Login")
            self.root.configure(background= "#1e3843") #configure background define a cor de fundo da janela (utilziar hexadecimal para cores mais agradáveis)
            self.root.geometry("300x350") #define o tamanho de inicio da janela horizontal x vertical
            self.root.resizable(False, False) #habilita se a tela vai ser responsiva quanto a modificação de tamanho true (horizontal), true (vertical)

        def widgets_login(self):
            self.userpic = PhotoImage(data=base64.b64decode(''))

            self.userpic = self.userpic.subsample(2,2)
            self.bt_userpic = Button(root,image=self.userpic)
            self.bt_userpic.place(relx=0.25,rely=0.1)

            #Image(root,bd=0,image=self.userpic)
            Label(root,text="Usuário:").place(relx=0.1,rely=0.6)
            Label(root,text="Senha:").place(relx=0.1,rely=0.7)
            


Application()